import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "./index.css";

// Dummy property data
const properties = [
  {
    id: 1,
    position: [40.7128, -74.006], // New York City
    address: "123 Broadway, NY",
    price: "$500,000",
  },
  {
    id: 2,
    position: [40.7282, -73.986], // East Village, NY
    address: "456 E 10th St, NY",
    price: "$750,000",
  },
  {
    id: 3,
    position: [40.758, -73.9855], // Times Square, NY
    address: "789 7th Ave, NY",
    price: "$1,200,000",
  },
];

const Home = ({ user }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [mapCenter, setMapCenter] = useState([40.7128, -74.006]); // Default: NYC

  // Filter properties based on search query
  const filteredProperties = properties.filter((property) =>
    property.address.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Update map center when a property is found (use first match)
  const handleSearch = () => {
    if (filteredProperties.length > 0) {
      setMapCenter(filteredProperties[0].position);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-900">
            Wealth<span className="text-gold-600">Map</span>
          </h1>
          <div className="text-sm text-gray-600">
            Welcome, {user?.email || "User"}
          </div>
        </div>
      </header>
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Property Map
        </h2>
        {/* Search Bar */}
        <div className="mb-4 flex items-center space-x-2">
          <input
            type="text"
            placeholder="Search properties by address..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleSearch}
            className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 transition duration-200">
            Search
          </button>
        </div>
        <div className="map-container">
          <MapContainer
            center={mapCenter}
            zoom={13}
            style={{ height: "500px", width: "100%" }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='Â© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {filteredProperties.map((property) => (
              <Marker key={property.id} position={property.position}>
                <Popup>
                  <div>
                    <h3 className="font-bold">{property.address}</h3>
                    <p>Price: {property.price}</p>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </main>
    </div>
  );
};

export default Home;
